import {useEffect} from 'react'
import Router from 'next/router'
import {isAuth} from '../action/auth'

const Private = ({children}) => {
  useEffect(() => {
    if(!Auth()) {
      Router.push('/signin')
    }
  }, [])
  return <React.Fragment>{children}</React.Fragment>
}

export default Private
